<?php/** * User: swapnil * Date: 3/9/12 * Time: 3:30 PM */Yii::import('zii.widgets.CPortlet');class LeftSidebar extends CPortlet{	public $option = 'vedic';	public $firstarr = array();	//public $secondarr = array();    protected function renderContent(){	switch($this->option)	{		case 'vedic':		$firstarr = array('Aarti'=>Yii::app()->createUrl('vedic/vedic',array('type'=>VedicType::Aarti)),						  'Mantra'=>Yii::app()->createUrl('vedic/vedic',array('type'=>VedicType::Mantra)),						  'Atharva Shirsha in Marathi'=>get_page_link(13),						  'Atharva Shirsha in English'=>get_page_link(16),						  'Ganesh Pooja Vidhi'=>'',						  '108 names of Lord ganesha'=>get_page_link(4),						  'Ganesha Mahima'=>Yii::app()->createUrl('experience/index'),					);		$secondarr = Vedic::model()->findAll();		$view ="vedicview";		$node_const = NodeType::Vedic;		break;		case 'temple':		$firstarr = array('Temples'=>Yii::app()->createUrl('temple/index',array('type'=>TempleType::Temple)),						  'Ganesh Mandals'=>Yii::app()->createUrl('temple/index',array('type'=>TempleType::Mandal)),						  'Ganesha Mahima'=>Yii::app()->createUrl('experience/index'),					);		$secondarr = Temple::model()->findAll();		$view ="templeview";		$node_const = NodeType::Temple;		break;		case 'recipe':		$firstarr = array('Prasad'=>Yii::app()->createUrl('recipe/index',array('type'=>RecipeType::Prasad)),						  'Naivaidya'=>Yii::app()->createUrl('recipe/index',array('type'=>RecipeType::Naivaidya)),						  'For diabetic patients'=>Yii::app()->createUrl('recipe/index',array('type'=>RecipeType::Diabetic)),						  'Ganesha Mahima'=>Yii::app()->createUrl('experience/index'),					);		$secondarr = recipe::model()->findAll();		$view ="recipeview";		$node_const = NodeType::Recipe;		break;		case 'experience':		$firstarr = array('Users Experience'=>Yii::app()->createUrl('experience/index',array('type'=>MahimaType::Experience)),						  'Wish to ganesh'=>Yii::app()->createUrl('experience/index',array('type'=>MahimaType::Wish)),					);		$secondarr = Experience::model()->findAll();		$view ="expview";		$node_const = NodeType::Experience;		break;		case 'user':		$firstarr = array(						'Change password'=>Yii::app()->createUrl('user/chngoldpass'),						'Aarti'=>Yii::app()->createUrl('vedic/vedic',array('type'=>VedicType::Aarti)),						  'Mantra'=>Yii::app()->createUrl('vedic/vedic',array('type'=>VedicType::Mantra)),						  'temple'=>Yii::app()->createUrl('temple/index'),						  'Atharva Shirsha in Marathi'=>get_page_link(13),						  'Atharva Shirsha in English'=>get_page_link(16),						  'Ganesh Pooja Vidhi'=>'',						  '108 names of Lord ganesha'=>get_page_link(4),					);		$secondarr = Vedic::model()->findAll();		$view ="vedicview";		$node_const = NodeType::Vedic;		break;		default:				$firstarr = array('Aarti'=>Yii::app()->createUrl('vedic/vedic',array('type'=>VedicType::Aarti)),						  'Mantra'=>Yii::app()->createUrl('vedic/vedic',array('type'=>VedicType::Mantra)),						  'temple'=>Yii::app()->createUrl('temple/index'),					);		$secondarr = Vedic::model()->findAll();		$view ="vedicview";		$node_const = NodeType::Vedic;	}        $this->render('left_sidebar',array('firstarr'=>$firstarr,'secondarr'=>$secondarr,'option'=>$this->option,'view'=>$view,'node_const'=>$node_const));    }}