	<div class="top-widget">		<div class="title-bar">Categories</div>		<?php foreach($firstarr as $key=>$val){?>		<div class="side-menu">&raquo; <a href="<?php echo $val; ?>"><?php echo $key;?></a></div>		<?php } ?>	</div>	<div class="top-widget mt10">		<div class="title-bar">See more</div>		<?php 		$url = $option."/".$view;		foreach($secondarr as $value){		?>		<div class="side-menu">&raquo; <a href="<?php echo Yii::app()->createUrl($url,array('slug'=>$value->slug));?>"><?php if($option != 'temple'){echo $value->title; }else{ echo $value->name; } ?></a></div>		<?php } ?>	</div>	<div class="top-widget mt10">		<div class="title-bar">Recent Posts</div><?php$args = array( 'posts_per_page' => 5);/* http://codex.wordpress.org/Function_Reference/wp_get_recent_posts */	$args = array(    'numberposts' => 5,    'category' => 0,    'orderby' => 'post_date',    'order' => 'DESC',    'post_status' => 'publish'	);$recent_posts = wp_get_recent_posts($args);	foreach( $recent_posts as $recent ){		echo '<div class="side-menu">&raquo;<a href="' . get_permalink($recent["ID"]) . '" title="Look '.esc_attr($recent["post_title"]).'" >' .   $recent["post_title"].'</a> </div> ';	}wp_reset_postdata();?>	</div>